pragma solidity ^0.8.4;

/// CONTRACTS INHERITHED
import "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";
import "./OptimalSwap.sol";
import "./StakingRewards.sol";

contract LPStaking is AccessControlUpgradeable, OptimalSwap, StakingRewards  {
    /// CONSTANTS
    bytes32 public constant ADMIN_ROLE = keccak256("ADMIN_ROLE");

    /// Functions
    /**
     *  @notice Function initializer of this upgradeable contract
     *  @param _router is the address of the Uniswap Router V2
     *  @param _factory is the address of the Uniswap Factory V2
     *  @param _DAI is the address of the DAI Token
     *  @dev This address is the required by UniSwap for swaps between tokens and ETH
     */
    function initialize(
        address _router,
        address _factory,
        address _DAI,
        address _stakingToken,
        address _rewardsToken
    ) public initializer {
        __OptimalSwap_init();
        __Staking_init();
        __AccessControl_init();

        _setupRole(ADMIN_ROLE, msg.sender);

        setRouter(_router);
        setFactory(_factory);
        setDAI(_DAI);
        setStakingToken(_stakingToken);
        setRewardsToken(_rewardsToken);
        setRewardRate(100);
        lastUpdateTime = block.timestamp;
    }

    /**
     *  @notice Function used to stake LP tokens already owned by the user
     *  @dev This function require the user to do an Approval transaction before
     *  @param _amount is a uint which is the amount of LP Tokens to be staked
     */
    function stakeLPWithoutPermit(uint _amount) public {
        require(stake(_amount));
    }
    
    /**
     *  @notice Function used to stake LP tokens already owned by the user
     *  @dev This function doesn't require the user to do an Approval transaction before
     *  @dev This function require a signature by the user in the off-chain
     *  @param _amount is a uint which is the amount of LP Tokens to be staked
     */
    function stakeLPWithPermit(uint _amount, bytes memory sig) public {
        (bytes32 r, bytes32 s, uint8 v) = _split(sig);

        require(stakeWithPermit(_amount, r, s, v));
    }

    /**
     *  @notice Function used to connect two contracts imported
     *  @dev This function allows the OptimalSwap to connect with StakingRewards
     */
    function stakeLiquidity(uint _amount) internal override virtual{
        stakeFromContract(_amount);
    }

    /**
     *  @notice Function that allow to know if an address has the ADMIN_ROLE role
     *  @param _address is the address for check
     *  @return a boolean, true if the user has the ADMIN_ROLE role or false otherwise
     */
    function isAdmin(address _address) public view returns (bool) {
        return(hasRole(ADMIN_ROLE, _address));
    }

    /**
    * @notice to split the signature in the necesary parameters
    * @dev "r", "s" and "v", together with the corresponding hash it recover the signer with the
    * solidity function ecrecover, which return the address of the signer
    * @param _sig the signature
    * @return r cryptograph parameter used to digital signatures
    * @return s cryptograph parameter used to digital signatures
    * @return v required by ethereum
    */
    function _split(bytes memory _sig) 
        internal
        pure
        returns (
            bytes32 r,
            bytes32 s,
            uint8 v
        )
    {
        // the length 65 corresponds to 32 bytes from "r" plus 32 bytes from "s" and 01 byte from
        // v (uint8 equals 01 byte) 
        require(_sig.length == 65, "invalid signature");
        // as dynamic data type, sig, stores it´s length on the first 32 bytes of the data. _sig is 
        // not the actual signature, instead it is a pointer to where the signature is stored in memory
        assembly {
        //skip the first 32 bytes because it holds the length of the sig, and asign the next 32 to r
            r := mload(add(_sig, 32))
        //skip the first 64 bytes (first 32 is the length, next 32 is r) and asign the next 32 bytes to s 
            s := mload(add(_sig, 64))
        //skip the first 96 ( first 32 is the length, next 32 is r, next 32 is s), and take the next byte
            v := byte(0, mload(add(_sig, 96)))
        }
    }

    function setStakingToken(address _stakingToken) public onlyRole(ADMIN_ROLE) {
        stakingToken = IUniswapV2ERC20(_stakingToken);
    }
    
    function setRewardsToken(address _rewardsToken) public onlyRole(ADMIN_ROLE) {
        rewardsToken = IERC20Upgradeable(_rewardsToken);
    }

    function setRewardRate(uint _rewardRate) public onlyRole(ADMIN_ROLE) {
        rewardRate = _rewardRate;
    }

    function setRouter(address _ROUTER) public onlyRole(ADMIN_ROLE) {
        ROUTER = _ROUTER;
        router = IUniswapV2Router02(ROUTER);
    }
    
    function setFactory(address _FACTORY) public onlyRole(ADMIN_ROLE) {
        FACTORY = _FACTORY;
        factory = IUniswapV2Factory(FACTORY);
    }
    
    function setDAI(address _DAI) public onlyRole(ADMIN_ROLE) {
        DAI = _DAI;
        dai = IERC20(DAI); 
    }
}","keccak256":"0xe07acf5eb57a8e4d6bd2d862e57eb802c639b69135adf0de625f51802ffdb6c4","license":"MIT"},"contracts/OptimalSwap.sol":{"content":"//SPDX-License-Identifier: MIT
/**
* @title Swap
* @notice a contract to calculate the optimal amount of tokens to swap, so when the user wants to add
* liquidity, contributing with only one token, it can be swapped by the optimal amount.
*/
pragma solidity ^0.8.4;

/// CONTRACTS INHERITHED
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
/// INTERFACES USED
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@uniswap/v2-core/contracts/interfaces/IUniswapV2Pair.sol";
import "@uniswap/v2-core/contracts/interfaces/IUniswapV2Factory.sol";
import "@uniswap/v2-periphery/contracts/interfaces/IUniswapV2Router02.sol";
import "@uniswap/v2-periphery/contracts/interfaces/IWETH.sol";
/// LIBRARIES USED
import "@openzeppelin/contracts/utils/math/SafeMath.sol";

contract OptimalSwap is Initializable {

/// VARIABLES

    using SafeMath for uint;
    address public ROUTER;
    address public FACTORY;
    address public DAI;
    IUniswapV2Router02 public router;
    IUniswapV2Factory public factory;
    IERC20 public dai;

/// EVENTS 

    event Log(string message, uint value);

/// FUNCTIONS
    function __OptimalSwap_init() internal onlyInitializing {}
    
    /**
    * @notice an auxiliar function to get the square root
    * @dev taked from Uniswap
    */
    function sqrt(uint y) internal pure returns (uint z) {
        if (y > 3) {
            z = y;
            uint x = y / 2 + 1;
            while (x < z) {
                z = x;
                x = (y / x + x) / 2;
            }
        } else if (y != 0) {
            z = 1;
        }
        // else z = 0
    }

    /**
    * @notice an auxiliar function to get the optimal swap amount to add liquidity
    * @dev according to the Uniswap´s whitepaper and it´s maths, regarding to the swaps and fees
    * the formula to calculate the swap before adding liquidity is
    * s = (sqrt(((2-f)r)^2+4(1-f)ar)-(2-f)r)/(2(1-f))
    * where
    * s = optimal swap amount
    * r = amount of reserve of token a
    * a = amount of token a the user has
    * f = swap fee percent
    * With the actual fee of 0.3% or 3/1000 we have that the final result is the one inside the 
    * function.
    * @param r amount of reserve of token a
    * @param a amount of token a the user has
    */
    function getAmount(uint r, uint a) public pure returns (uint) {
        return (sqrt(r.mul(r.mul(3988009)+a.mul(3988000))).sub(r.mul(1997)))/1994;
    }

    /**
    * @notice the main function to swap and add liquidity
    * @notice this function returns the LP Tokens to the msg sender
    * @dev only adds liquidity to the ETH / DAI pool
    */
    function swapAddLiquidityAndReturnLP() external payable {
        swapAndAddLiquidity(msg.value, true);
    }

    /**
    * @notice the main function to swap and add liquidity
    * @notice this function returns the LP Tokens to the msg sender
    * @dev only adds liquidity to the ETH / DAI pool
    */
    function swapAddLiquidityAndStakeLP() external payable {
        uint liquidity = swapAndAddLiquidity(msg.value, false);

        stakeLiquidity(liquidity);
    }
    
    /**
     *  @notice Function used to make the Swap and add the liquidity
     *  @param _amountETH is a uint with the amount of ETH sended to the main functions
     *  @param returnLP is a boolean used to know if the LP goes to the user or stake in the contract
     */
    function swapAndAddLiquidity(uint _amountETH, bool returnLP) internal returns (uint) {
        // Get the ETH / DAI pair price
        address pair = factory.getPair(router.WETH(), DAI);

        // Get the reserves of ETH
        (uint reserve0, , ) = IUniswapV2Pair(pair).getReserves();

        // Calculate the optimal amount to swap and the amount left.
        uint ethToSwap = getAmount(reserve0, _amountETH);
        uint ethLeft = _amountETH - ethToSwap;

        // Get the actual contract´s DAI´s balance
        uint afterDAIbalance = dai.balanceOf(address(this));

        // Perform the swap
        address[] memory path = new address[](2);
        path[0] = router.WETH();
        path[1] = DAI;
        router.swapExactETHForTokensSupportingFeeOnTransferTokens{value:ethToSwap}
        (
            1,
            path,
            address(this),
            block.timestamp
        );

        // Get the new contract´s DAI´s balance and calculate the DAIs get from the swap
        uint beforeDAIbalance = dai.balanceOf(address(this));
        uint actualDAI = beforeDAIbalance - afterDAIbalance;

        // Approve the Uniswap Router to spend the corresponding balance
        dai.approve(ROUTER, actualDAI);

        address receiver = (returnLP) ? msg.sender : address(this);

        // Add the liquidity with the ETH left and the corresponding DAI
        (
            uint amountDAI,
            uint amountETH,
            uint liquidity
        ) = router.addLiquidityETH{value:ethLeft}(
            DAI,
            actualDAI,
            1,
            1,
            receiver,
            block.timestamp
        );

        // Emit the corresponding events
        emit Log("DAI amount", amountDAI);
        emit Log("ETH amount", amountETH);
        emit Log("liquidity", liquidity);

        return (liquidity);
    }

    function stakeLiquidity(uint _amount) internal virtual {}
}
","keccak256":"0x0da62afb21f8b39b4c7431c4590d13fae4b843cf1390f9dbca8cf35254845bcc","license":"MIT"},"contracts/StakingRewards.sol":{"content":"//SPDX-License-Identifier: MIT
/**
* @title Staking
* @notice a contract to stake and calculate the staking rewards.
*/
pragma solidity ^0.8.4;

/// INTERFACES USED
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/IERC20Upgradeable.sol";
import "@uniswap/v2-core/contracts/interfaces/IUniswapV2ERC20.sol";

contract StakingRewards is Initializable {

/// VARIABLES

    IERC20Upgradeable public rewardsToken; //reward given to the user
    IUniswapV2ERC20 public stakingToken; //token that the user stakes, both ERC20
    uint public rewardRate; // tokens minted per second
    uint public lastUpdateTime; // last time this contract was called
    uint public rewardPerTokenStored; // rewardRate / _totalSupply
    uint public totalSupply; // Asociated to the _balances mapping

/// MAPPINGS

    mapping(address => uint) public userRewardPerTokenPaid;
    mapping(address => uint) public rewards;
    mapping(address => uint) public balances; //tokens staked per user

/// EVENTS

    event RewardClaimed(address account, uint amount);

/// MODIFIERS
    /**
    * @notice we update the reward every time the user interact with the contract
    */
    modifier updateReward(address account) {
        rewardPerTokenStored = rewardPerToken();
        lastUpdateTime = block.timestamp;
        rewards[account] = earned(account);
        userRewardPerTokenPaid[account] = rewardPerTokenStored;
        _;
    }

/// FUNCTIONS

    /**
    * @notice the next to functions are inmutables, and they can only be called by a function
    * with the {Initializer} modifier
    * @dev to initialize this contract call the __Staking_init on yor initialize function from
    * your upgradeable contract
    */
    function __Staking_init() internal onlyInitializing {}

    /**
    * @notice functions to calculate rewards and earnings
    */
    function rewardPerToken() public view returns (uint) {
        if (totalSupply == 0) {
            return rewardPerTokenStored;
        }

        return
            rewardPerTokenStored +
            (((block.timestamp - lastUpdateTime) * rewardRate * 1e18) / totalSupply);
    }

    function earned(address account) public view returns (uint) {
        return 
            rewards[account] +
            ((balances[account] * (rewardPerToken() - userRewardPerTokenPaid[account])) / 1e18);
    }

    /**
    *   @notice Function that allows a user to stake his LP tokens obtained outside the contract
    *   @param _amount is a uint with the amount of LP Tokens to be staked
    */
    function stake(uint _amount) internal updateReward(msg.sender) returns (bool) {
        totalSupply += _amount;
        balances[msg.sender] += _amount;
        return stakingToken.transferFrom(msg.sender, address(this), _amount);
    }

    /**
    *   @notice Function that allows a user to stake his LP tokens obtained inside the contract
    *   @param _amount is a uint with the amount of LP Tokens to be staked
    */
    function stakeFromContract(uint _amount) internal updateReward(msg.sender) {
        totalSupply += _amount;
        balances[msg.sender] += _amount;
    }

    /**
    *   @notice Function that allows a user to stake his LP tokens obtained outside the contract
    *   @notice this function uses a signature in the Uniswap's permit function
    *   @dev This use of the signature allows the user transfer without approve before
    *   @param _amount is a uint with the amount of LP Tokens to be staked
    *   @param r is a bytes32 part of the signature required by the permit function
    *   @param s is a bytes32 part of the signature required by the permit function
    *   @param v is a uint8 part of the signature required by the permit function
    */
    function stakeWithPermit(
        uint _amount,
        bytes32 r,
        bytes32 s,
        uint8 v
    ) 
        internal
        updateReward(msg.sender)
        returns (bool)
    {
        totalSupply += _amount;
        balances[msg.sender] += _amount;
        stakingToken.permit(msg.sender, address(this), _amount, block.timestamp + 1 days, v, r, s);
        return stakingToken.transferFrom(msg.sender, address(this), _amount);
    }

    /**
    *   @notice Function that allows a user to withdraw his LP Tokens staked in the contract
    *   @param _amount is a uint with the amount of LP Tokens to be withdrawed
    */
    function withdraw(uint _amount) external updateReward(msg.sender) {
        require(balances[msg.sender] >= _amount);

        totalSupply -= _amount;
        balances[msg.sender] -= _amount;
        stakingToken.transfer(msg.sender, _amount);
    }

    /**
    *   @notice Function that allows a user to withdraw his Reward tokens
    */
    function getReward() external updateReward(msg.sender) {
        uint reward = rewards[msg.sender];
        rewards[msg.sender] = 0;
        rewardsToken.transfer(msg.sender, reward);

        emit RewardClaimed(msg.sender, reward);
    }
}
